-- Typeable things are convertible to our format
class Typeable a where
    toType :: a -> String
    toType a = "unknown"
    toJson :: a -> Value
    default toJson :: ToJSON a => a -> Value
    toJson = toJSON
    -- Function needed to unwrap IO values without needing calls to unsafeIO
    toJsonIO :: a -> IO (String, Value)
    default toJsonIO :: a -> IO (String, Value)
    toJsonIO a = return ((toType a), (toJson a))

-- We support maybe as the null value
instance (Typeable a) => Typeable (Maybe a) where
    toType m = case m of
        Just n -> toType n
        Nothing -> "nothing"
    toJson m = case m of
        Just n -> toJson n
        Nothing -> Null

instance (Typeable a) => Typeable (IO a) where
    -- Calls to `unsafePerformIO` may not produces the expected results, there for `toJsonIO` is preferred, when called
    -- this function, the student has written no clean haskell code
    {-# NOINLINE toType #-}
    toType = toType . unsafePerformIO
    -- Calls to `unsafePerformIO` may not produces the expected results, there for `toJsonIO` is preferred, when called
    -- this function, the student has written no clean haskell code
    {-# NOINLINE toJson #-}
    toJson = toJson . unsafePerformIO
    -- Preferred function call to pass the IO monad results to the judge, avoid `unsafePerformIO`
    toJsonIO m = do
        unwrapped <- m
        toJsonIO unwrapped

instance Typeable W.Word8 where toType _ = "uint8"
instance Typeable W.Word16 where toType _ = "uint16"
instance Typeable W.Word32 where toType _ = "uint32"
instance Typeable W.Word64 where toType _ = "uint64"
instance Typeable I.Int8 where toType _ = "int8"
instance Typeable I.Int16 where toType _ = "int16"
instance Typeable I.Int32 where toType _ = "int32"
instance Typeable I.Int64 where toType _ = "int64"

instance Typeable Float where
    toType _ = "single_precision"
    toJson f =
        if (isNaN f) then (toJSON ("nan":: Text))
        else if (isInfinite f) && f < 0 then (toJSON ("-inf" :: Text))
        else if (isInfinite f) && f > 0 then (toJSON ("inf" :: Text))
        else (toJSON f)
instance Typeable Double where
    toType _ = "double_precision"
    toJson f =
        if (isNaN f) then (toJSON ("nan":: Text))
        else if (isInfinite f) && f < 0 then (toJSON ("-inf" :: Text))
        else if (isInfinite f) && f > 0 then (toJSON ("inf" :: Text))
        else (toJSON f)
instance Typeable Integer where toType _ = "bigint"
instance Typeable Int where toType _ = "integer"

instance Typeable Bool where toType _ = "boolean"
instance Typeable Char where toType _ = "char"

instance Typeable Text where toType _ = "text"
instance {-# OVERLAPPABLE #-} Typeable String where toType _ = "text"

instance {-# OVERLAPPABLE #-} Typeable a => Typeable [a] where
    toType m = "list"
    toJson l = toJSON (map toObject l)

