toObject :: Typeable a => a -> Value
toObject value = object ["type" .= toJSON (toType value), "data" .= toJson value]


sendValue :: Typeable a => FilePath -> a -> IO ()
sendValue file value = do
    (jsonType, jsonData) <- toJsonIO value
    LBS.appendFile file (encode (object [ "type" .= toJSON jsonType, "data" .= jsonData ]))

sendValueH :: Typeable a => Handle -> a -> IO ()
sendValueH file value = do
    (jsonType, jsonData) <- toJsonIO value
    LBS.hPutStr file (encode (object [ "type" .= toJSON jsonType, "data" .= jsonData ]))

toMessage :: Message -> Value
toMessage m = object [
        "description" .= toJSON (description m),
        "format" .= toJSON (format m),
        "permission" .= toJSON (permission m)
    ]

sendEvaluated :: FilePath -> EvaluationResult -> IO ()
sendEvaluated file r =
    LBS.appendFile file (encode (object [
        "result" .= toJSON (result r),
        "readable_expected" .= toJSON (readableExpected r),
        "readable_actual" .= toJSON (readableActual r),
        "messages" .= toJSON (map toMessage (messages r))
    ]))

sendEvaluatedH :: Handle -> EvaluationResult -> IO ()
sendEvaluatedH file r =
    LBS.hPutStr file (encode (object [
        "result" .= toJSON (result r),
        "readable_expected" .= toJSON (readableExpected r),
        "readable_actual" .= toJSON (readableActual r),
        "messages" .= toJSON (map toMessage (messages r))
    ]))

sendException :: Exception e => FilePath -> Maybe e -> IO ()
sendException file (Just ex) = LBS.appendFile file (encode (object [
        "message" .= toJSON (show ex),
        "stacktrace" .= toJSON (show ex)
    ]))
sendException _ Nothing = return ()
